<?phpclass Wcategories extends AppModel{    public $useTable = "wiki__wcategories";    public function __makeCondition($search)    {        if ((string)(int)$search == $search) {            return array(                'id' => intval($search)            );        } else {            return array(                'name' => $search            );        }    }    public function getCategoriesBySearch($key, $search)    {        $search_user = $this->find('first', array('conditions' => $this->__makeCondition($search)));        return (!empty($search_user)) ? $search_user['Wcategories'][$key] : NULL;    }    public function getAllFromCategories($search = null)    {        $search_user = $this->find('first', array('conditions' => $this->__makeCondition($search)));        if (!empty($search_user)) {            return ($search_user) ? $search_user['Wcategories'] : NULL;        }        return array();    }}